  (Modal.confirm && Modal.confirm('Once you submit this record for approval, you might not be able to edit it or recall it from the approval process depending on your settings. Continue?')) ||
  (!Modal.confirm && window.confirm('Once you submit this record for approval, you might not be able to edit it or recall it from the approval process depending on your settings. Continue?'))
) {
  window.location.href = '/p/process/Submit?retURL=%2F' + '{!Opportunity.Id}';
